import fs from 'fs';

// Read package.json
const pkg = JSON.parse(fs.readFileSync('./package.json', 'utf-8'));

// Generate TypeScript content
const content = `/* eslint-disable */
// Automatically generated by gen-package-info.js

export const packageInfo = {
  name: '${pkg.name}',
  version: '${pkg.version}'
};
`;

const outputPath = './src/packageInfo.ts';

fs.writeFileSync(outputPath, content, 'utf-8');

console.log(`âœ… packageInfo.ts generated: ${outputPath}`);
